mapper.select:
select * from( select a.*, rownum rn from (
select t.*, (select count(1) from teamlist l where l.t_num=t.t_num) as teamListCount from team t order by t_num
) a ) b where rn between #{first} and #{last}

SELECT *
FROM team
WHERE t_num = #{t_num}

select * from( select a.*, rownum rn from (
select t.id, m.name, m.pos, t.t_author from teamlist t join members m on t.id = m.id where t.t_num = #{t_num} order by 4 desc
) a ) b where rn between #{first} and #{last}

SELECT COUNT(*) AS TEAM
FROM teamlist
WHERE t_num = #{t_num}

SELECT n_no, t_title, t_date
FROM tnotice
WHERE t_num = #{t_num}

SELECT *
FROM tnotice
WHERE t_num = #{t_num} AND n_no = #{n_no}

SELECT *
FROM members
WHERE id = #{id}

mapper.update:
update members 
set t_num = #{t_num} 
where id=#{id}

update members 
set t_num = null 
where id=#{id}

mapper.select:
select * from( select a.*, rownum rn from (
	select img
	from tgallery
	where t_num = #{t_num}
) a ) b where rn between #{first} and #{last}

select * from( select a.*, rownum rn from (
	SELECT * 
	FROM tinvite
	WHERE t_num = #{t_num}
) a ) b where rn between #{first} and #{last}

SELECT * 
FROM tinvite
WHERE id = #{id}

mapper.delete:
DELETE FROM tinvite
WHERE t_num = #{t_num}
AND id = #{id}

mapper.select:
SELECT count(*)
FROM tournamentteam
WHERE t_no = #{t_no}

mapper.update:
update members 
set point=to_number(#{point})-100000 
where id=#{id}

update tournament
set t_point=to_number(#{t_point})+100000
where t_no=#{t_no}

mapper.select:
select *
from comeInvite
where id = #{id}

select *
from comeInvite
