mapper.select:
SELECT id,name
from members
where id=#{id}

select *
from members
where id=#{id} and pw=#{pw}

select m_id "_id",title "title" ,type "type",starttime "start", endtime "end",comm "description",backgroundcolor "backgroundColor",id "id"
from rfield
where f_id=#{f_id}

select f.*
,nvl((select likeit from likeit where num=f.num and id=#{id}),0) as heartstate
from fboard f where num=#{num}

select likeit
from likeit
where num=#{num}
and id=#{id}

select price
from field
where f_id=#{f_id}

select t_name from
team
where t_num=#{t_num}

select t_num
from members
where id=#{id}

select name
from field
where f_id=#{f_id}

select concat(location1,location2)
from field
where f_id=#{f_id}
