mapper.update:
update members set
point =
point -#{point}
where id= #{id}

mapper.select:
select d.m_no,d.m_type,d.f_name,d.f_address,d.price,d.lv,d.p_max,d.m_id, substr(d.m_date, 12, 5) as m_hour,substr(d.m_date, 1,10) as m_date, substr(d.m_date, 1,4) as m_year,substr(d.m_date, 6,2) as m_month,substr(d.m_date,9, 2) as m_day, substr(d.m_date, 12,2) as m_ho, d.t_type,d.f_id,nvl(f,0)f
,nvl((select 1 from pplayers where m_no=d.m_no and id=#{id}),0) as sel
from p_match d, ( select a.m_no ,count(*) f from p_match a,pplayers b where a.m_no = b.m_no group by a.m_no )c where d.m_no = c.m_no(+) and d.m_no = #{m_no}

select * from players
where t_num = #{t_num} and m_no=
#{m_no}

mapper.update:
update teammatch set so_num = #{t_num} , so_name =
#{so_name}
where m_no = #{m_no}

mapper.select:
select 1 from manageraply
where
id=#{id}

select a.name,a.id,b.m_no from members a,pplayers b
where
a.id=b.id and b.m_no = #{m_no}

select 1 from teammatch
where so_num = #{t_num}
and substr(m_date,1,13) = substr(#{m_date},1,13)
